import React, { useState } from 'react';

const Content = () => {
    const greetlist = ['hello', 'hi', 'hola', 'namaste'];
    const rand = greetlist[Math.floor(Math.random() * greetlist.length)];
    
    const [count, setCount] = useState(0);
    const [greet, setGreet] = useState("Good Morning");

    const handleClick = () => {
        if (count === 0) {
            setCount(count + 1);
        } else if (count === 1) {
            setGreet("Good Afternoon");
            setCount(count + 1);
        }
    };

    return (
        <div>
            <h3>{greet}  students, welcome all!!</h3>
            <button onClick={handleClick}>{count} click me</button>
        </div>
    );
};

export default Content;
